    String novafrase;
    private TextView textoNovaFrase;

    @Override
    public View onCreateView(LayoutInflater layoutInflater, ViewGroup viewGroup, Bundle bundle) {
        View inflate = layoutInflater.inflate(R.layout.fragment_series, viewGroup, false);
        this.textoNovaFrase = (TextView) inflate.findViewById(R.id.textoNovaFrase);
        this.botaoNovaFrase = (Button) inflate.findViewById(R.id.botaoNovaFrase);
        this.btshare = (ImageButton) inflate.findViewById(R.id.btshare);
        this.botaoNovaFrase.setOnClickListener(new View.OnClickListener() {
            @Override // android.view.View.OnClickListener
            public void onClick(View view) {
                int nextInt = new Random().nextInt(SeriesFragment.this.frases.length);
                SeriesFragment.this.textoNovaFrase.setText(SeriesFragment.this.frases[nextInt]);
                SeriesFragment seriesFragment = SeriesFragment.this;
                seriesFragment.novafrase = seriesFragment.frases[nextInt];
            }
        });
        this.btshare.setOnClickListener(new View.OnClickListener() {
            @Override // android.view.View.OnClickListener
            public void onClick(View view) {
                shareText();
            }

            public void shareText() {
                Intent intent = new Intent("android.intent.action.SEND");
                intent.setType("text/plain");
               // intent.addFlags(524288);
                intent.putExtra("android.intent.extra.TEXT", SeriesFragment.this.novafrase);
                SeriesFragment.this.startActivity(intent);
            }
        });
        return inflate;
    }
